import React from 'react';
import { Drawer, List, ListItem, ListItemText,  Button } from '@mui/material';
import { Link } from 'react-router-dom';
import { makeStyles } from '@mui/styles';


const useStyles = makeStyles((theme) => ({ //this is a hook which is a function that returns an object of styles, a hook is a function that starts with use and lets you hook into react features
  drawer: {
    width: 240,
    flexShrink: 0,
  },
  drawerPaper: {
    width: 240,
  },
}));

function Sidebar({ open, onClose }) {
  const classes = useStyles();

  const handleDrawerClose = () => {
    onClose();
  };

  return (
    <Drawer
      className={classes.drawer}
      classes={{
        paper: classes.drawerPaper,
      }}
      anchor="left"
      open={open}
      onClose={handleDrawerClose}
    >
      <List>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemText primary="Home" />
        </ListItem>
        <ListItem Button component={Link} to="/login" onClick={handleDrawerClose}>
          <ListItemText primary="About" />
        </ListItem>
        <ListItem Button component={Link} to="/profile" onClick={handleDrawerClose}>
          <ListItemText primary="Contact" />
        </ListItem>
      </List>
    </Drawer>
  );
}

export default Sidebar;
