import React from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Typography} from '@mui/material';
import { Link } from 'react-router-dom';
import HomeIcon from '@mui/icons-material/Home';
import InventoryIcon from '@mui/icons-material/Inventory';
import TimelineIcon from '@mui/icons-material/Timeline';
import StorefrontIcon from '@mui/icons-material/Storefront';
import ArticleIcon from '@mui/icons-material/Article';

function Sidebar({ open, onClose }) {

  const handleDrawerClose = () => {
    onClose();
  };

  return (
    <Drawer
    variant="temporary"
      anchor="left"
      open={open}
      onClose={handleDrawerClose}
      display="flex"
      sx={{
        width: 180, // This width is equal to the width of the Sidebar component
        flexShrink: 0,
        '& .MuiDrawer-paper': { 
          width: 180, 
          backgroundColor: 'f5f5f5', // Add a background color
        },
      }}
    >
      <Typography variant="body" sx={{mt: '1rem', ml: '1rem'}} fontSize={28} fontFamily={'Roboto'} color={'#000000'}>
        HawkHub
      </Typography>
      <List sx={{mt: '2rem'}}>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemIcon sx={{ minWidth: 32 }}>
            <HomeIcon sx={{ marginBottom: '4px' }} />
          </ListItemIcon>
          <ListItemText primary="Home" />
        </ListItem>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemIcon sx={{ minWidth: 32 }}>
            <InventoryIcon sx={{ marginBottom: '4px' }} />
          </ListItemIcon>
          <ListItemText primary="StockTracker" />
        </ListItem>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemIcon sx={{ minWidth: 32 }}>
            <TimelineIcon sx={{ marginBottom: '4px' }} />
          </ListItemIcon>
          <ListItemText primary="FinanceTracker" />
        </ListItem>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemIcon sx={{ minWidth: 32 }}>
            <StorefrontIcon sx={{ marginBottom: '4px' }} />
          </ListItemIcon>
          <ListItemText primary="Suppliers" />
        </ListItem>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemIcon sx={{ minWidth: 32 }}>
             sx={{ marginBottom: '4px' }} />
          </ListItemIcon>
          <ListItemText primary="Guide" />
        </ListItem>
      </List>
      <List sx={{mt: '2rem'}}>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemText primary="Notification" />
        </ListItem>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemText primary="Support" />
        </ListItem>
      </List>
    </Drawer>
  );
}

export default Sidebar;
