import { 
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAIL,

    USER_LOGOUT,

    USER_REGISTER_REQUEST,
    USER_REGISTER_SUCCESS,
    USER_REGISTER_FAIL,

    USER_DETAILS_REQUEST,
    USER_DETAILS_SUCCESS,
    USER_DETAILS_FAIL,
    USER_DETAILS_RESET,

    USER_UPDATE_PROFILE_REQUEST,
    USER_UPDATE_PROFILE_SUCCESS,
    USER_UPDATE_PROFILE_FAIL,
    USER_UPDATE_PROFILE_RESET,

} from "../constants/userConstants";

export const userLoginReducer = (state = {}, action) => { //start with an initial state of an empty object 
    switch (action.type) {
        case USER_LOGIN_REQUEST:
            return { loading: true } //return a new state with loading set to true
        case USER_LOGIN_SUCCESS:
            return { loading: false, userInfo: action.payload } //return a new state with loading set to false and userInfo set to the payload of the action
        case USER_LOGIN_FAIL:
            return { loading: false, error: action.payload } //return a new state with loading set to false and error set to the payload of the action
        case USER_LOGOUT:
            return {} //return an empty object
        default:
            return state
    }
}

export const userRegisterReducer = (state = {}, action) => { 
    switch (action.type) {
        case USER_REGISTER_REQUEST:
            return { loading: true } 
        case USER_REGISTER_SUCCESS:
            return { loading: false, userInfo: action.payload } 
        case USER_REGISTER_FAIL:
            return { loading: false, error: action.payload } 
        case USER_LOGOUT:
            return {}
        default:
            return state
    }
}

export const userDetailsReducer = (state = { user: {} }, action) => {
    switch (action.type) {
        case USER_DETAILS_REQUEST:
            return { ...state, loading: true }

        case USER_DETAILS_SUCCESS:
            return { loading: false, user: action.payload }

        case USER_DETAILS_FAIL:
            return { loading: false, error: action.payload }

        case USER_DETAILS_RESET:
            return { user: {} }

        default:
            return state
    }
}

export const userUpdateProfileReducer = (state = {}, action) => {
    switch (action.type) {
        case USER_UPDATE_PROFILE_REQUEST:
            return { loading: true }

        case USER_UPDATE_PROFILE_SUCCESS:
            return { loading: false, success: true, userInfo: action.payload }

        case USER_UPDATE_PROFILE_FAIL:
            return { loading: false, error: action.payload }

        case USER_UPDATE_PROFILE_RESET:
            return {}

        default:
            return state
    }
}
