import React from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Typography} from '@mui/material';
import { Link } from 'react-router-dom';
import HomeIcon from '@mui/icons-material/Home';

function Sidebar({ open, onClose }) {

  const handleDrawerClose = () => {
    onClose();
  };

  return (
    <Drawer
    variant="temporary"
      anchor="left"
      open={open}
      onClose={handleDrawerClose}
      display="flex"
      sx={{
        width: 170, // This width is equal to the width of the Sidebar component
        flexShrink: 0,
        '& .MuiDrawer-paper': { 
          width: 170, 
          backgroundColor: 'f5f5f5', // Add a background color
        },
      }}
    >
      <Typography variant="body" sx={{mt: '1rem', ml: '1rem'}} fontSize={28} fontFamily={'Roboto'} color={'#000000'}>
        HawkHub
      </Typography>
      <List sx={{mt: '2rem'}}>
        <ListItem Button component={Link} to="/" onClick={handleDrawerClose}>
          <ListItemIcon sx={{ minWidth: 32 }}>
            <HomeIcon sx={{ marginBottom: '4px' }} />
          </ListItemIcon>
          <ListItemText primary="Home" />
        </ListItem>
        <ListItem Button component={Link} to="/login" onClick={handleDrawerClose}>
          <ListItemText primary="StockTracker" />
        </ListItem>
        <ListItem Button component={Link} to="/profile" onClick={handleDrawerClose}>
          <ListItemText primary="Finance" />
        </ListItem>
      </List>
    </Drawer>
  );
}

export default Sidebar;
