import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { AppBar, Toolbar, Typography, Button, Container, IconButton, Menu, MenuItem } from '@mui/material';
import { Link } from 'react-router-dom';
import { logout } from '../actions/userActions';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCartOutlined';
import AccountCircleIcon from '@mui/icons-material/AccountCircleOutlined';

function Header() {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const dispatch = useDispatch();

  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const logoutHandler = () => {
    dispatch(logout());
  };

  return (
    <AppBar position="static" color="primary" sx={{ backgroundColor: 'orange' }}>
      <Container>
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Typography variant="h6" component={Link} to="/" sx={{ textDecoration: 'none', color: 'inherit' }}>
            HawkHub
          </Typography>
          <div >
            <Button component={Link} to="/cart" color="inherit" startIcon={<ShoppingCartIcon />} sx={{ fontSize: '1rem' }}>
              Cart
            </Button>

            {userInfo ? (
              <React.Fragment>
                <IconButton
                  color="inherit"
                  endIcon={<AccountCircleIcon />}
                  id="user-menu"
                  sx={{ padding: '6px', fontSize: '1.3rem' }}
                  onClick={handleMenuOpen}

                >
                  {userInfo.name}
                </IconButton>

                <Menu id="user-menu" anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
                  <MenuItem component={Link} to="/profile" onClick={handleMenuClose}>
                    Profile
                  </MenuItem>

                  <MenuItem onClick={logoutHandler}>Logout</MenuItem>
                </Menu>
              </React.Fragment>
            ) : (
              <Button component={Link} to="/login" color="inherit" startIcon={<AccountCircleIcon />}>
                Login
              </Button>
            )}
          </div>
        </Toolbar>
      </Container>
    </AppBar>
  );
}

export default Header;
